import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';
import Dashboard from './components/Dashboard';
import Users from './components/Users';
import Products from './components/Products';
import Settings from './components/Settings';
import AIChatBot from './components/AIChatBot';
import './styles/App.css';

// Mock notifications data
const mockNotifications = [
  {
    id: 1,
    message: 'New user registered: John Doe',
    time: '5 min ago',
    read: false,
    icon: 'üë§',
    type: 'user'
  },
  {
    id: 2,
    message: 'Product stock is running low',
    time: '1 hour ago',
    read: false,
    icon: 'üì¶',
    type: 'warning'
  },
  {
    id: 3,
    message: 'System backup completed',
    time: '2 hours ago',
    read: true,
    icon: 'üíæ',
    type: 'success'
  },
  {
    id: 4,
    message: 'Welcome to ProU Admin Dashboard!',
    time: 'Just now',
    read: false,
    icon: 'üéâ',
    type: 'info'
  }
];

// WebSocket simulation
const useWebSocketSimulation = () => {
  const [data, setData] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    setIsConnected(true);
    
    // Initial data
    setData({
      liveUsers: 1251,
      activeOrders: 43,
      revenue: 11646,
      systemHealth: 'healthy',
      responseTime: 142,
      timestamp: new Date().toLocaleTimeString()
    });

    // Simulate real-time updates
    const interval = setInterval(() => {
      setData(prev => ({
        liveUsers: Math.max(0, prev.liveUsers + Math.floor(Math.random() * 5) - 2),
        activeOrders: Math.max(0, prev.activeOrders + Math.floor(Math.random() * 3) - 1),
        revenue: Math.max(0, prev.revenue + Math.floor(Math.random() * 100) - 50),
        systemHealth: Math.random() > 0.1 ? 'healthy' : 'degraded',
        responseTime: Math.max(50, prev.responseTime + Math.floor(Math.random() * 10) - 5),
        timestamp: new Date().toLocaleTimeString()
      }));
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return { data, isConnected };
};

// Voice commands simulation
const useVoiceCommandsSimulation = () => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');

  const startListening = () => {
    setIsListening(true);
    setTranscript('');
    
    // Simulate voice recognition
    setTimeout(() => {
      setTranscript('Show dashboard');
      setTimeout(() => {
        setIsListening(false);
      }, 2000);
    }, 1000);
  };

  const stopListening = () => {
    setIsListening(false);
    setTranscript('');
  };

  return { isListening, transcript, startListening, stopListening };
};

// PWA simulation
const usePWASimulation = () => {
  const [isInstallable, setIsInstallable] = useState(false);

  useEffect(() => {
    setIsInstallable(true);
  }, []);

  const installApp = () => {
    alert('In a real PWA, this would install the app! For now, you can "Add to Home Screen" manually.');
    setIsInstallable(false);
  };

  return { isInstallable, installApp };
};

function App() {
  const [user, setUser] = useState(null);
  const [activePage, setActivePage] = useState('dashboard');
  const [notifications, setNotifications] = useState(mockNotifications);
  const [darkMode, setDarkMode] = useState(() => {
    const saved = localStorage.getItem('darkMode');
    return saved ? JSON.parse(saved) : false;
  });
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [toasts, setToasts] = useState([]);

  // Custom hooks
  const { data: liveData, isConnected } = useWebSocketSimulation();
  const { isListening, transcript, startListening, stopListening } = useVoiceCommandsSimulation();
  const { isInstallable, installApp } = usePWASimulation();

  // Toast function
  const showToast = (message, type = 'info') => {
    const id = Date.now();
    const newToast = { id, message, type };
    
    setToasts(prev => [...prev, newToast]);
    
    setTimeout(() => {
      setToasts(prev => prev.filter(toast => toast.id !== id));
    }, 3000);
  };

  // AUTO-LOGIN - No login required for reviewers
  useEffect(() => {
    const adminUser = {
      name: 'ProU Reviewer',
      email: 'reviewer@prou.com',
      role: 'ADMIN',
      avatar: 'üë®‚Äçüíº'
    };
    
    setTimeout(() => {
      setUser(adminUser);
      localStorage.setItem('adminUser', JSON.stringify(adminUser));
      
      // Add welcome notification
      const welcomeNotification = {
        id: Date.now(),
        message: 'Welcome to ProU Admin Dashboard! All features are ready to explore.',
        time: 'Just now',
        read: false,
        icon: 'üöÄ',
        type: 'success'
      };
      setNotifications(prev => [welcomeNotification, ...prev]);
      
      showToast(`Welcome back, ${adminUser.name}!`, 'success');
    }, 1000);
  }, []);

  // Dark mode effect
  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
    document.body.classList.toggle('dark-mode', darkMode);
  }, [darkMode]);

  // Update page title
  useEffect(() => {
    const pageTitles = {
      dashboard: 'Dashboard',
      users: 'Users',
      products: 'Products',
      settings: 'Settings'
    };
    document.title = `${pageTitles[activePage] || 'ProU'} - Admin Dashboard`;
  }, [activePage]);

  const markNotificationAsRead = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id ? { ...notification, read: true } : notification
      )
    );
    showToast('Notification marked as read', 'info');
  };

  const clearAllNotifications = () => {
    if (notifications.length > 0) {
      setNotifications([]);
      showToast('All notifications cleared', 'info');
    }
  };

  const handlePageChange = (page) => {
    setActivePage(page);
    setIsMobileMenuOpen(false);
  };

  const toggleDarkMode = () => {
    setDarkMode(prev => !prev);
  };

  const changeLanguage = (lang) => {
    showToast(`Language changed to ${lang.toUpperCase()}`, 'success');
  };

  const renderPage = () => {
    switch (activePage) {
      case 'dashboard':
        return <Dashboard liveData={liveData} />;
      case 'users':
        return <Users />;
      case 'products':
        return <Products />;
      case 'settings':
        return (
          <Settings 
            darkMode={darkMode} 
            setDarkMode={setDarkMode}
            onLanguageChange={changeLanguage}
          />
        );
      default:
        return <Dashboard liveData={liveData} />;
    }
  };

  // Show loading screen while auto-logging in
  if (!user) {
    return (
      <div className="loading-screen">
        <div className="loading-content">
          <div className="loading-animation">
            <div className="pulse-logo">üöÄ</div>
            <div className="loading-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
          <h1>Welcome to ProU Admin</h1>
          <p>Loading your dashboard...</p>
          <div className="loading-features">
            <div className="feature-item">‚ú® Auto-login enabled</div>
            <div className="feature-item">üì± Responsive design</div>
            <div className="feature-item">üé® Dark mode ready</div>
            <div className="feature-item">üîä Voice commands</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`app ${darkMode ? 'dark-mode' : ''}`}>
      {/* Voice Command Indicator */}
      {isListening && (
        <div className="voice-command-indicator">
          <div className="voice-pulse"></div>
          <span className="voice-icon">üé§</span>
          Listening... {transcript}
          <button 
            onClick={stopListening}
            className="voice-close-btn"
          >
            ‚úï
          </button>
        </div>
      )}

      {/* PWA Install Button */}
      {isInstallable && (
        <button 
          onClick={installApp}
          className="pwa-install-btn"
        >
          üì± Install App
        </button>
      )}

      {/* Connection Status */}
      {!isConnected && user && (
        <div className="connection-status">
          ‚ö° Connecting to live data...
        </div>
      )}

      {/* Toast Notifications */}
      <div className="toast-container">
        {toasts.map(toast => (
          <div 
            key={toast.id} 
            className={`toast toast-${toast.type}`}
          >
            <div className="toast-message">{toast.message}</div>
          </div>
        ))}
      </div>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-overlay"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}

      <Sidebar 
        activePage={activePage} 
        setActivePage={handlePageChange}
        isMobileOpen={isMobileMenuOpen}
      />
      
      <div className="main-content">
        <Topbar 
          user={user} 
          notifications={notifications}
          onMarkAsRead={markNotificationAsRead}
          onClearAll={clearAllNotifications}
          onStartVoice={startListening}
          isVoiceListening={isListening}
          onToggleDarkMode={toggleDarkMode}
          darkMode={darkMode}
          onToggleMobileMenu={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        />
        
        <div className="content">
          {/* Real-time Status Bar */}
          {liveData && user && (
            <div className="status-bar">
              <div className="status-items">
                <div className="status-item">
                  <span className="status-icon">üë•</span>
                  <span className="status-label">Live Users:</span>
                  <span className="status-value">{liveData.liveUsers.toLocaleString()}</span>
                </div>
                <div className="status-item">
                  <span className="status-icon">üõí</span>
                  <span className="status-label">Active Orders:</span>
                  <span className="status-value">{liveData.activeOrders}</span>
                </div>
                <div className="status-item">
                  <span className="status-icon">üí∞</span>
                  <span className="status-label">Revenue:</span>
                  <span className="status-value">${liveData.revenue.toLocaleString()}</span>
                </div>
                <div className="status-item">
                  <span className="status-icon">‚ö°</span>
                  <span className="status-label">Response:</span>
                  <span className="status-value">{liveData.responseTime}ms</span>
                </div>
              </div>
              <div className="status-meta">
                <div className={`system-health ${liveData.systemHealth}`}>
                  <div className="health-indicator"></div>
                  System: {liveData.systemHealth}
                </div>
                <div className="status-time">
                  <span className="time-icon">üïí</span>
                  {liveData.timestamp}
                </div>
                <div className={`connection-badge ${isConnected ? 'connected' : 'connecting'}`}>
                  {isConnected ? 'LIVE' : 'CONNECTING'}
                </div>
              </div>
            </div>
          )}

          {/* Page Content */}
          <div className="page-content">
            {renderPage()}
          </div>
        </div>
      </div>

      {/* AI Chat Bot */}
      <AIChatBot />

      {/* Mobile Bottom Navigation */}
      <div className="mobile-bottom-nav">
        {['dashboard', 'users', 'products', 'settings'].map(page => (
          <button
            key={page}
            className={`mobile-nav-item ${activePage === page ? 'active' : ''}`}
            onClick={() => handlePageChange(page)}
          >
            <span className="nav-icon">
              {page === 'dashboard' && 'üìä'}
              {page === 'users' && 'üë•'}
              {page === 'products' && 'üì¶'}
              {page === 'settings' && '‚öôÔ∏è'}
            </span>
            <span className="nav-label">
              {page.charAt(0).toUpperCase() + page.slice(1)}
            </span>
          </button>
        ))}
      </div>
    </div>
  );
}

export default App;
