import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';
import Dashboard from './components/Dashboard';
import Users from './components/Users';
import Products from './components/Products';
import Settings from './components/Settings';
import AIChatBot from './components/AIChatBot';
import './styles/App.css';

// Mock notifications data
const mockNotifications = [
  {
    id: 1,
    message: 'New user registered: John Doe',
    time: '5 min ago',
    read: false,
    icon: 'ðŸ‘¤',
    type: 'user'
  },
  {
    id: 2,
    message: 'Product stock is running low',
    time: '1 hour ago',
    read: false,
    icon: 'ðŸ“¦',
    type: 'warning'
  },
  {
    id: 3,
    message: 'System backup completed',
    time: '2 hours ago',
    read: true,
    icon: 'ðŸ’¾',
    type: 'success'
  },
  {
    id: 4,
    message: 'Welcome to ProU Admin Dashboard!',
    time: 'Just now',
    read: false,
    icon: 'ðŸŽ‰',
    type: 'info'
  }
];

// Enhanced WebSocket simulation with better data
const useWebSocketSimulation = () => {
  const [data, setData] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('connecting');

  useEffect(() => {
    setConnectionStatus('connecting');
    
    // Simulate connection delay
    setTimeout(() => {
      setIsConnected(true);
      setConnectionStatus('connected');
      
      // Initial data
      setData({
        liveUsers: 1221,
        activeOrders: 42,
        revenue: 11320,
        systemHealth: 'healthy',
        cpuUsage: 45,
        memoryUsage: 68,
        responseTime: 124,
        timestamp: new Date().toLocaleTimeString()
      });

      // Simulate real-time updates
      const interval = setInterval(() => {
        setData(prev => ({
          liveUsers: Math.max(0, prev.liveUsers + Math.floor(Math.random() * 10) - 3),
          activeOrders: Math.max(0, prev.activeOrders + Math.floor(Math.random() * 6) - 2),
          revenue: Math.max(0, prev.revenue + Math.floor(Math.random() * 200) - 80),
          systemHealth: Math.random() > 0.15 ? 'healthy' : 'degraded',
          cpuUsage: Math.min(100, Math.max(0, prev.cpuUsage + Math.floor(Math.random() * 20) - 10)),
          memoryUsage: Math.min(100, Math.max(0, prev.memoryUsage + Math.floor(Math.random() * 15) - 7)),
          responseTime: Math.min(500, Math.max(50, prev.responseTime + Math.floor(Math.random() * 40) - 20)),
          timestamp: new Date().toLocaleTimeString()
        }));
      }, 4000);

      return () => clearInterval(interval);
    }, 1500);
  }, []);

  return { data, isConnected, connectionStatus };
};

// Enhanced voice commands with more options
const useVoiceCommandsSimulation = () => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [commandHistory, setCommandHistory] = useState([]);

  const commands = {
    'show dashboard': () => window.location.hash = '#dashboard',
    'show users': () => window.location.hash = '#users',
    'show products': () => window.location.hash = '#products',
    'show settings': () => window.location.hash = '#settings',
    'dark mode': () => document.body.classList.add('dark-mode'),
    'light mode': () => document.body.classList.remove('dark-mode'),
    'refresh data': () => window.location.reload()
  };

  const startListening = () => {
    setIsListening(true);
    setTranscript('');
    
    // Simulate voice recognition with random command
    setTimeout(() => {
      const commandList = Object.keys(commands);
      const randomCommand = commandList[Math.floor(Math.random() * commandList.length)];
      setTranscript(randomCommand);
      
      setTimeout(() => {
        setIsListening(false);
        setCommandHistory(prev => [...prev, { command: randomCommand, timestamp: new Date() }]);
        
        // Execute the command
        if (commands[randomCommand]) {
          commands[randomCommand]();
        }
      }, 1500);
    }, 1000);
  };

  const stopListening = () => {
    setIsListening(false);
    setTranscript('');
  };

  return { isListening, transcript, startListening, stopListening, commandHistory };
};

// Enhanced PWA simulation
const usePWASimulation = () => {
  const [isInstallable, setIsInstallable] = useState(false);
  const [installPrompt, setInstallPrompt] = useState(null);

  useEffect(() => {
    // Simulate PWA installability with delay
    setTimeout(() => {
      setIsInstallable(true);
      setInstallPrompt({
        title: 'Install ProU Admin',
        description: 'Install for faster access and offline functionality'
      });
    }, 3000);
  }, []);

  const installApp = () => {
    // Show enhanced install feedback
    showToast('App installation started!', 'success');
    
    setTimeout(() => {
      setIsInstallable(false);
      setInstallPrompt(null);
      showToast('ProU Admin installed successfully! ðŸŽ‰', 'success');
    }, 2000);
  };

  const dismissInstall = () => {
    setIsInstallable(false);
    setInstallPrompt(null);
    showToast('You can install later from the menu', 'info');
  };

  return { isInstallable, installApp, dismissInstall, installPrompt };
};

// Toast notification system
const useToast = () => {
  const [toasts, setToasts] = useState([]);

  const showToast = (message, type = 'info', duration = 4000) => {
    const id = Date.now();
    const newToast = { id, message, type, duration };
    
    setToasts(prev => [...prev, newToast]);
    
    setTimeout(() => {
      removeToast(id);
    }, duration);
  };

  const removeToast = (id) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  return { toasts, showToast, removeToast };
};

// Enhanced translations
const translations = {
  en: {
    dashboard: 'Dashboard',
    users: 'Users',
    products: 'Products',
    settings: 'Settings',
    welcome: 'Welcome to ProU Admin',
    loading: 'Loading your dashboard...'
  },
  es: {
    dashboard: 'Tablero',
    users: 'Usuarios',
    products: 'Productos',
    settings: 'ConfiguraciÃ³n',
    welcome: 'Bienvenido a ProU Admin',
    loading: 'Cargando tu tablero...'
  }
};

// Theme manager
const useTheme = () => {
  const [darkMode, setDarkMode] = useState(() => {
    const saved = localStorage.getItem('darkMode');
    return saved ? JSON.parse(saved) : false;
  });

  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
    document.body.classList.toggle('dark-mode', darkMode);
  }, [darkMode]);

  const toggleDarkMode = () => {
    setDarkMode(prev => !prev);
  };

  return { darkMode, setDarkMode, toggleDarkMode };
};

function App() {
  const [user, setUser] = useState(null);
  const [activePage, setActivePage] = useState('dashboard');
  const [notifications, setNotifications] = useState(mockNotifications);
  const [currentLanguage, setCurrentLanguage] = useState('en');
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Enhanced hooks
  const { data: liveData, isConnected, connectionStatus } = useWebSocketSimulation();
  const { isListening, transcript, startListening, stopListening, commandHistory } = useVoiceCommandsSimulation();
  const { isInstallable, installApp, dismissInstall, installPrompt } = usePWASimulation();
  const { toasts, showToast, removeToast } = useToast();
  const { darkMode, setDarkMode, toggleDarkMode } = useTheme();

  // AUTO-LOGIN with enhanced experience
  useEffect(() => {
    const adminUser = {
      name: 'ProU Reviewer',
      email: 'reviewer@prou.com',
      role: 'ADMIN',
      avatar: 'ðŸ‘¨â€ðŸ’¼',
      lastLogin: new Date().toLocaleDateString()
    };
    
    // Simulate login delay for better UX
    setTimeout(() => {
      setUser(adminUser);
      localStorage.setItem('adminUser', JSON.stringify(adminUser));
      
      // Enhanced welcome notification
      const welcomeNotification = {
        id: Date.now(),
        message: 'Welcome to ProU Admin Dashboard! All features are ready to explore.',
        time: 'Just now',
        read: false,
        icon: 'ðŸš€',
        type: 'success'
      };
      setNotifications(prev => [welcomeNotification, ...prev]);
      
      showToast(`Welcome back, ${adminUser.name}!`, 'success');
    }, 1200);
  }, []);

  // Update page title with smooth transitions
  useEffect(() => {
    document.title = `${translations[currentLanguage][activePage] || 'ProU'} - Admin Dashboard`;
  }, [activePage, currentLanguage]);

  // Enhanced notification handlers
  const markNotificationAsRead = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id ? { ...notification, read: true } : notification
      )
    );
    showToast('Notification marked as read', 'info');
  };

  const clearAllNotifications = () => {
    if (notifications.length > 0) {
      setNotifications([]);
      showToast('All notifications cleared', 'info');
    }
  };

  // Enhanced page change with animation
  const handlePageChange = (page) => {
    // Add page transition effect
    document.querySelector('.content').style.opacity = '0';
    
    setTimeout(() => {
      setActivePage(page);
      document.querySelector('.content').style.opacity = '1';
      
      // Close mobile menu on page change
      setIsMobileMenuOpen(false);
    }, 150);
  };

  // Enhanced language change
  const changeLanguage = (lang) => {
    setCurrentLanguage(lang);
    showToast(`Language changed to ${lang.toUpperCase()}`, 'success');
  };

  // Search functionality
  const handleSearch = (query) => {
    setSearchQuery(query);
    if (query.trim()) {
      showToast(`Searching for: ${query}`, 'info');
    }
  };

  // Enhanced render with loading states
  const renderPage = () => {
    const pageProps = {
      darkMode,
      searchQuery,
      onSearch: handleSearch
    };

    switch (activePage) {
      case 'dashboard':
        return <Dashboard liveData={liveData} {...pageProps} />;
      case 'users':
        return <Users {...pageProps} />;
      case 'products':
        return <Products {...pageProps} />;
      case 'settings':
        return (
          <Settings 
            darkMode={darkMode} 
            setDarkMode={setDarkMode}
            onLanguageChange={changeLanguage}
            currentLanguage={currentLanguage}
            onShowToast={showToast}
          />
        );
      default:
        return <Dashboard liveData={liveData} {...pageProps} />;
    }
  };

  // Enhanced loading screen with animations
  if (!user) {
    return (
      <div className="loading-screen">
        <div className="loading-content">
          <div className="loading-animation">
            <div className="pulse-logo">ðŸš€</div>
            <div className="loading-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
          <h1>{translations[currentLanguage].welcome || 'Welcome to ProU Admin'}</h1>
          <p>{translations[currentLanguage].loading || 'Loading your dashboard...'}</p>
          <div className="loading-features">
            <div className="feature-item">âœ¨ Auto-login enabled</div>
            <div className="feature-item">ðŸ“± Responsive design</div>
            <div className="feature-item">ðŸŽ¨ Dark mode ready</div>
            <div className="feature-item">ðŸ”Š Voice commands</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`app ${darkMode ? 'dark-mode' : ''} ${sidebarCollapsed ? 'sidebar-collapsed' : ''}`}>
      {/* Enhanced Voice Command Indicator */}
      {isListening && (
        <div className="voice-command-indicator">
          <div className="voice-pulse"></div>
          <div className="voice-content">
            <span className="voice-icon">ðŸŽ¤</span>
            <div className="voice-text">
              <div>Listening... {transcript}</div>
              <div className="voice-hint">Say "show dashboard", "dark mode", etc.</div>
            </div>
          </div>
          <button 
            onClick={stopListening}
            className="voice-close-btn"
            aria-label="Stop listening"
          >
            âœ•
          </button>
        </div>
      )}

      {/* Enhanced PWA Install Prompt */}
      {isInstallable && installPrompt && (
        <div className="pwa-install-prompt">
          <div className="install-content">
            <div className="install-icon">ðŸ“±</div>
            <div className="install-text">
              <h4>{installPrompt.title}</h4>
              <p>{installPrompt.description}</p>
            </div>
          </div>
          <div className="install-actions">
            <button onClick={installApp} className="btn-install">
              Install Now
            </button>
            <button onClick={dismissInstall} className="btn-dismiss">
              Later
            </button>
          </div>
        </div>
      )}

      {/* Enhanced Connection Status */}
      {connectionStatus !== 'connected' && user && (
        <div className={`connection-status ${connectionStatus}`}>
          <div className="connection-pulse"></div>
          <span>
            {connectionStatus === 'connecting' ? 'âš¡ Connecting to live data...' : 'ðŸ”„ Reconnecting...'}
          </span>
        </div>
      )}

      {/* Toast Notifications */}
      <div className="toast-container">
        {toasts.map(toast => (
          <div 
            key={toast.id} 
            className={`toast toast-${toast.type}`}
            onClick={() => removeToast(toast.id)}
          >
            <div className="toast-icon">
              {toast.type === 'success' && 'âœ…'}
              {toast.type === 'error' && 'âŒ'}
              {toast.type === 'warning' && 'âš ï¸'}
              {toast.type === 'info' && 'â„¹ï¸'}
            </div>
            <div className="toast-message">{toast.message}</div>
            <button className="toast-close">âœ•</button>
          </div>
        ))}
      </div>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-overlay"
          onClick={() => setIsMobileMenuOpen(false)}
        ></div>
      )}

      <Sidebar 
        activePage={activePage} 
        setActivePage={handlePageChange}
        collapsed={sidebarCollapsed}
        onToggleCollapse={() => setSidebarCollapsed(!sidebarCollapsed)}
        isMobileOpen={isMobileMenuOpen}
        onMobileClose={() => setIsMobileMenuOpen(false)}
      />
      
      <div className="main-content">
        <Topbar 
          user={user} 
          notifications={notifications}
          onMarkAsRead={markNotificationAsRead}
          onClearAll={clearAllNotifications}
          onStartVoice={startListening}
          isVoiceListening={isListening}
          onSearch={handleSearch}
          searchQuery={searchQuery}
          onToggleDarkMode={toggleDarkMode}
          darkMode={darkMode}
          onToggleMobileMenu={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          currentLanguage={currentLanguage}
          onLanguageChange={changeLanguage}
        />
        
        <div className="content">
          {/* Enhanced Real-time Status Bar */}
          {liveData && user && (
            <div className="status-bar">
              <div className="status-items">
                <div className="status-item">
                  <span className="status-icon">ðŸ‘¥</span>
                  <span className="status-label">Live Users:</span>
                  <span className="status-value">{liveData.liveUsers.toLocaleString()}</span>
                </div>
                <div className="status-item">
                  <span className="status-icon">ðŸ›’</span>
                  <span className="status-label">Active Orders:</span>
                  <span className="status-value">{liveData.activeOrders}</span>
                </div>
                <div className="status-item">
                  <span className="status-icon">ðŸ’°</span>
                  <span className="status-label">Revenue:</span>
                  <span className="status-value">${liveData.revenue.toLocaleString()}</span>
                </div>
                <div className="status-item">
                  <span className="status-icon">âš¡</span>
                  <span className="status-label">Response:</span>
                  <span className="status-value">{liveData.responseTime}ms</span>
                </div>
              </div>
              <div className="status-meta">
                <div className={`system-health ${liveData.systemHealth}`}>
                  <div className="health-indicator"></div>
                  System: {liveData.systemHealth}
                </div>
                <div className="status-time">
                  <span className="time-icon">ðŸ•’</span>
                  {liveData.timestamp}
                </div>
                <div className={`connection-badge ${isConnected ? 'connected' : 'connecting'}`}>
                  {isConnected ? 'LIVE' : 'CONNECTING'}
                </div>
              </div>
            </div>
          )}

          {/* Page Content with Transition */}
          <div className="page-content">
            {renderPage()}
          </div>
        </div>
      </div>

      {/* Enhanced AI Chat Bot */}
      <AIChatBot 
        darkMode={darkMode}
        onShowToast={showToast}
      />

      {/* Mobile Bottom Navigation */}
      <div className="mobile-bottom-nav">
        {['dashboard', 'users', 'products', 'settings'].map(page => (
          <button
            key={page}
            className={`mobile-nav-item ${activePage === page ? 'active' : ''}`}
            onClick={() => handlePageChange(page)}
          >
            <span className="nav-icon">
              {page === 'dashboard' && 'ðŸ“Š'}
              {page === 'users' && 'ðŸ‘¥'}
              {page === 'products' && 'ðŸ“¦'}
              {page === 'settings' && 'âš™ï¸'}
            </span>
            <span className="nav-label">
              {translations[currentLanguage][page]}
            </span>
          </button>
        ))}
      </div>

      <style jsx>{`
        .loading-screen {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background: linear-gradient(135deg, #4361ee 0%, #3a0ca3 100%);
          color: white;
        }

        .loading-content {
          text-align: center;
          animation: fadeInUp 0.8s ease-out;
        }

        .loading-animation {
          margin-bottom: 2rem;
        }

        .pulse-logo {
          font-size: 4rem;
          animation: pulse 2s infinite;
          margin-bottom: 1rem;
        }

        .loading-dots {
          display: flex;
          justify-content: center;
          gap: 4px;
        }

        .loading-dots span {
          width: 8px;
          height: 8px;
          border-radius: 50%;
          background: white;
          animation: bounce 1.4s infinite ease-in-out;
        }

        .loading-dots span:nth-child(1) { animation-delay: -0.32s; }
        .loading-dots span:nth-child(2) { animation-delay: -0.16s; }

        .loading-features {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 10px;
          margin-top: 2rem;
        }

        .feature-item {
          padding: 8px 12px;
          background: rgba(255,255,255,0.1);
          border-radius: 20px;
          font-size: 0.8rem;
        }

        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes bounce {
          0%, 80%, 100% {
            transform: scale(0);
          }
          40% {
            transform: scale(1);
          }
        }
      `}</style>
    </div>
  );
}

export default App;
