export const roles = {
  SUPER_ADMIN: ['*'],
  ADMIN: ['dashboard', 'users:read', 'users:write', 'products:read', 'products:write', 'settings'],
  MANAGER: ['dashboard', 'users:read', 'products:read', 'products:write'],
  USER: ['dashboard', 'products:read']
};

export const hasPermission = (userRole, permission) => {
  if (!userRole || !roles[userRole]) return false;
  
  if (roles[userRole].includes('*')) return true;
  
  return roles[userRole].includes(permission);
};

export const canAccessPage = (userRole, page) => {
  const pagePermissions = {
    dashboard: 'dashboard',
    users: 'users:read',
    products: 'products:read',
    settings: 'settings'
  };
  
  return hasPermission(userRole, pagePermissions[page]);
};
