import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';
import Dashboard from './components/Dashboard';
import Users from './components/Users';
import Products from './components/Products';
import Settings from './components/Settings';
import Login from './components/Login';
import AIPredictions from './components/AIPredictions';
import { useWebSocket } from './hooks/useWebSocket';
import { useVoiceCommands } from './hooks/useVoiceCommands';
import { usePWA } from './hooks/usePWA';
import { getLanguage, setLanguage, translations } from './i18n/translations';
import { canAccessPage } from './utils/rbac';
import './styles/App.css';

// Mock notifications data
const mockNotifications = [
  {
    id: 1,
    message: 'New user registered: John Doe',
    time: '5 min ago',
    read: false,
    icon: 'ðŸ‘¤'
  },
  {
    id: 2,
    message: 'Product stock is running low',
    time: '1 hour ago',
    read: false,
    icon: 'ðŸ“¦'
  },
  {
    id: 3,
    message: 'System backup completed',
    time: '2 hours ago',
    read: true,
    icon: 'ðŸ’¾'
  },
  {
    id: 4,
    message: 'New login from unknown device',
    time: '1 day ago',
    read: true,
    icon: 'ðŸ”’'
  }
];

function App() {
  const [user, setUser] = useState(null);
  const [activePage, setActivePage] = useState('dashboard');
  const [darkMode, setDarkMode] = useState(false);
  const [notifications, setNotifications] = useState(mockNotifications);
  const [currentLanguage, setCurrentLanguage] = useState(getLanguage());

  // Advanced Hooks
  const { data: liveData, isConnected } = useWebSocket('ws://localhost:8080');
  const { isListening, transcript, startListening, stopListening } = useVoiceCommands();
  const { isInstallable, installApp } = usePWA();

  // Check if user is logged in on app start
  useEffect(() => {
    const savedUser = localStorage.getItem('adminUser');
    if (savedUser) {
      const userData = JSON.parse(savedUser);
      setUser(userData);
    }
  }, []);

  // Update page title based on active page and language
  useEffect(() => {
    document.title = `${translations[currentLanguage][activePage] || 'ProU'} - Admin Dashboard`;
  }, [activePage, currentLanguage]);

  const handleLogin = (userData) => {
    const userWithRole = {
      ...userData,
      role: userData.role || 'ADMIN' // Default role
    };
    
    setUser(userWithRole);
    localStorage.setItem('adminUser', JSON.stringify(userWithRole));
    
    // Add login notification
    const newNotification = {
      id: Date.now(),
      message: `Welcome back, ${userData.name}!`,
      time: 'Just now',
      read: false,
      icon: 'ðŸŽ‰'
    };
    setNotifications(prev => [newNotification, ...prev]);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('adminUser');
    setActivePage('dashboard');
    
    // Add logout notification
    const logoutNotification = {
      id: Date.now(),
      message: 'You have been logged out',
      time: 'Just now',
      read: false,
      icon: 'ðŸšª'
    };
    setNotifications(prev => [logoutNotification, ...prev]);
  };

  const changeLanguage = (lang) => {
    setLanguage(lang);
    setCurrentLanguage(lang);
    
    // Add language change notification
    const langNotification = {
      id: Date.now(),
      message: `Language changed to ${lang.toUpperCase()}`,
      time: 'Just now',
      read: false,
      icon: 'ðŸŒ'
    };
    setNotifications(prev => [langNotification, ...prev]);
  };

  const markNotificationAsRead = (id) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id ? { ...notification, read: true } : notification
      )
    );
  };

  const clearAllNotifications = () => {
    setNotifications([]);
  };

  const handlePageChange = (page) => {
    if (user && canAccessPage(user.role, page)) {
      setActivePage(page);
    } else {
      alert('You do not have permission to access this page.');
    }
  };

  const renderPage = () => {
    if (!user) {
      return <Login onLogin={handleLogin} currentLanguage={currentLanguage} />;
    }

    // Check if user has permission to access the page
    if (!canAccessPage(user.role, activePage)) {
      return (
        <div style={{ 
          display: 'flex', 
          flexDirection: 'column', 
          alignItems: 'center', 
          justifyContent: 'center', 
          height: '50vh',
          textAlign: 'center'
        }}>
          <div style={{ fontSize: '48px', marginBottom: '20px' }}>ðŸš«</div>
          <h2>Access Denied</h2>
          <p>You don't have permission to access this page.</p>
          <button 
            className="btn btn-primary"
            onClick={() => setActivePage('dashboard')}
          >
            Go to Dashboard
          </button>
        </div>
      );
    }

    switch (activePage) {
      case 'dashboard':
        return (
          <div>
            <Dashboard liveData={liveData} currentLanguage={currentLanguage} />
            <AIPredictions />
          </div>
        );
      case 'users':
        return <Users currentLanguage={currentLanguage} />;
      case 'products':
        return <Products currentLanguage={currentLanguage} />;
      case 'settings':
        return (
          <Settings 
            darkMode={darkMode} 
            setDarkMode={setDarkMode}
            currentLanguage={currentLanguage}
            onLanguageChange={changeLanguage}
          />
        );
      default:
        return <Dashboard liveData={liveData} currentLanguage={currentLanguage} />;
    }
  };

  // Show login page if not authenticated
  if (!user) {
    return <Login onLogin={handleLogin} currentLanguage={currentLanguage} />;
  }

  return (
    <div className={`app ${darkMode ? 'dark-mode' : ''}`}>
      {/* Voice Command Indicator */}
      {isListening && (
        <div style={{
          position: 'fixed',
          top: '20px',
          right: '20px',
          background: 'linear-gradient(45deg, #4361ee, #3a0ca3)',
          color: 'white',
          padding: '12px 18px',
          borderRadius: '25px',
          zIndex: 10000,
          display: 'flex',
          alignItems: 'center',
          gap: '10px',
          boxShadow: '0 4px 15px rgba(67, 97, 238, 0.3)',
          fontSize: '14px',
          fontWeight: '500'
        }}>
          <div style={{
            width: '12px',
            height: '12px',
            borderRadius: '50%',
            background: '#ff4757',
            animation: 'pulse 1s infinite'
          }}></div>
          ðŸŽ¤ Listening... {transcript}
          <button 
            onClick={stopListening}
            style={{
              background: 'rgba(255,255,255,0.2)',
              border: 'none',
              color: 'white',
              borderRadius: '50%',
              width: '24px',
              height: '24px',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            âœ•
          </button>
        </div>
      )}

      {/* PWA Install Button */}
      {isInstallable && (
        <button 
          onClick={installApp}
          style={{
            position: 'fixed',
            bottom: '20px',
            right: '20px',
            background: 'linear-gradient(45deg, #4361ee, #3a0ca3)',
            color: 'white',
            border: 'none',
            padding: '12px 20px',
            borderRadius: '25px',
            cursor: 'pointer',
            zIndex: 10000,
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            boxShadow: '0 4px 15px rgba(67, 97, 238, 0.4)',
            fontSize: '14px',
            fontWeight: '500',
            transition: 'transform 0.2s'
          }}
          onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
          onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
        >
          ðŸ“± Install App
        </button>
      )}

      {/* WebSocket Connection Status */}
      {!isConnected && user && (
        <div style={{
          position: 'fixed',
          top: '20px',
          left: '50%',
          transform: 'translateX(-50%)',
          background: '#ff9800',
          color: 'white',
          padding: '8px 16px',
          borderRadius: '20px',
          zIndex: 9999,
          fontSize: '12px',
          display: 'flex',
          alignItems: 'center',
          gap: '8px'
        }}>
          âš¡ Connecting to live data...
        </div>
      )}

      <Sidebar 
        activePage={activePage} 
        setActivePage={handlePageChange}
        userRole={user?.role}
        currentLanguage={currentLanguage}
      />
      
      <div className="main-content">
        <Topbar 
          user={user} 
          notifications={notifications}
          onLogout={handleLogout}
          onMarkAsRead={markNotificationAsRead}
          onClearAll={clearAllNotifications}
          onStartVoice={startListening}
          currentLanguage={currentLanguage}
          onLanguageChange={changeLanguage}
          isVoiceListening={isListening}
        />
        
        <div className="content">
          {/* Real-time Status Bar */}
          {liveData && user && (
            <div style={{
              background: 'linear-gradient(90deg, #e3f2fd, #f3e5f5)',
              padding: '8px 20px',
              borderBottom: '1px solid var(--border)',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              fontSize: '12px',
              color: '#666'
            }}>
              <div style={{ display: 'flex', gap: '20px' }}>
                <span>ðŸ‘¥ Live Users: <strong>{liveData.liveUsers}</strong></span>
                <span>ðŸ›’ Active Orders: <strong>{liveData.activeOrders}</strong></span>
                <span>ðŸ’° Revenue: <strong>${liveData.revenue}</strong></span>
              </div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '5px',
                  color: liveData.systemHealth === 'healthy' ? '#4caf50' : '#ff9800'
                }}>
                  <div style={{
                    width: '8px',
                    height: '8px',
                    borderRadius: '50%',
                    background: liveData.systemHealth === 'healthy' ? '#4caf50' : '#ff9800',
                    animation: liveData.systemHealth === 'healthy' ? 'pulse 2s infinite' : 'none'
                  }}></div>
                  System: {liveData.systemHealth}
                </div>
                <span>ðŸ•’ {liveData.timestamp}</span>
              </div>
            </div>
          )}

          {renderPage()}
        </div>
      </div>

      <style jsx>{`
        @keyframes pulse {
          0% { opacity: 1; }
          50% { opacity: 0.5; }
          100% { opacity: 1; }
        }
      `}</style>
    </div>
  );
}

export default App;
