import React from 'react';

const Topbar = () => {
  return (
    <div className="topbar">
      <div className="search-bar">
        <input 
          type="text" 
          placeholder="Search..." 
          style={{
            padding: '8px 15px',
            border: '1px solid var(--border)',
            borderRadius: '20px',
            width: '300px'
          }}
        />
      </div>
      
      <div className="user-info">
        <div className="notifications">
          <span>ðŸ””</span>
        </div>
        <div className="user-profile">
          <div style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            background: 'var(--primary)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: 'white',
            fontWeight: 'bold'
          }}>
            SR
          </div>
          <span>Siddarth R K</span>
        </div>
      </div>
    </div>
  );
};

export default Topbar;
